import React, {Component} from "react";
import {
    List, message, Avatar, Spin,
} from 'antd';

import InfiniteScroll from 'react-infinite-scroller';

class ArticleItem extends Component {
    state = {
        userLists: [],
        pages: '',
        page: '',
        limit: '',
        count: '',
        loading: false,
        hasMore: true,
    }

    componentDidMount() {
      this.fetchData();
      console.log(this.state);
    }

    fetchData = () => {
        var url ="http://localhost:8888/front/getBlogLists";
        this.blogsRes = fetch(url,{
            method: 'GET',
            mode: 'cors',
        }).then(res => {
            console.log(res);
            return res.json();
        }).then(json => {
            console.log('获取的结果', json);
            this.setState({
                articles: json.articles,
                pages: json.pages,
                page: json.page,
                limit: json.limit,
                count: json.count
            });
            return json;
        }).catch(err => {
            console.log('请求错误', err);        });
    }

    handleInfiniteOnLoad = () => {
      let data = this.state.articles;
      this.setState({
        loading: true,
      });
      if (data.length > 14) {
        message.warning('Infinite List loaded all');
        this.setState({
          hasMore: false,
          loading: false,
        });
        return;
      }
      this.fetchData((res) => {
        data = data.concat(res.results);
        this.setState({
          data,
          loading: false,
        });
      });
    }

    render() {
      return (
        <div className="demo-infinite-container">
          <InfiniteScroll
            initialLoad={false}
            pageStart={0}
            loadMore={this.handleInfiniteOnLoad}
            hasMore={!this.state.loading && this.state.hasMore}
            useWindow={false}
          >
            <List
              dataSource={this.state.articles}
              renderItem={item => (
                <List.Item key={item._id}>
                  <List.Item.Meta
                    avatar={<Avatar src="https://zos.alipayobjects.com/rmsportal/ODTLcjxAfvqbxHnVXCYX.png" />}
                    title={<a href="https://ant.design">{item.article_title}</a>}
                    description={<p><span>{item.author}</span><span>{item.createTime}</span></p>}
                  />
                  <div>{item.author}</div>
                </List.Item>
              )}
            >
              {this.state.loading && this.state.hasMore && (
                <div className="demo-loading-container">
                  <Spin />
                </div>
              )}
            </List>
          </InfiniteScroll>
        </div>
      );
    }
  }

  export default ArticleItem;
