import React, { Component } from 'react';
import { Layout, Breadcrumb } from 'antd';
import Event from '../../assets/events.js';
import axios from 'axios';
const { Content } = Layout;

class ArticleDetail extends Component {
    constructor(){
        super();
        this.state = {
            cur_id: '',
            articlesDetail:''
        }
    }
    componentDidMount(){
        var that = this;
        Event.on('detail',msg =>{
            // console.log('dd'+msg.data.articlesDetail[0].contentall);
            var content= msg.data.articlesDetail[0] ? msg.data.articlesDetail[0].contentall : 'no data';
            that.setState({
                articlesDetail: content
            },()=>{
                that.getDetail();
            });
            this.forceUpdate();
        })
    }
    getDetail(){
        var con = document.querySelector('#contentDom');
        con.innerHTML = this.state.articlesDetail;
        console.log(this.state.articlesDetail);
    }
    render() {
        return (
            <Layout>
                <div id='contentDom'></div>
            </Layout>
        )
    }
}
export default ArticleDetail;
