import React, {Component} from "react";
// import fetch from 'whatwg-fetch';

class BlogLists extends Component {
    constructor(props) {
        super(props);
        this.state = {
            username: '',
            lastGistUrl: '',
            userLists: []
        };
    }
    componentDidMount() {
        var url ="http://localhost:8888/front/getBlogLists";
        this.blogsRes = fetch(url,{
            method: 'GET',
            mode: 'cors',
        }).then(res => {
            console.log(res);
            return res.text();
        }).then(json => {
            console.log('获取的结果', json);
            this.setState({
                userLists: json.articles,
                pages: json.pages,
                page: json.page,
                limit: json.limit,
                count: json.count
            });
            return json;
        }).catch(err => {
            console.log('请求错误', err);
        });
    }
    componentWillUnmount() {
      this.blogsRes.abort();
    }
    render() {
        return (
            <div>
