import React, {Component} from "react";
import {withRouter,Link} from 'react-router-dom'
import {
    List, message, Avatar, Spin,Button
} from 'antd';
import axios from 'axios';
import InfiniteScroll from 'react-infinite-scroller';
import Event from '../../../assets/events.js';
class ArticleItem extends Component {
    constructor(props) {
        super(props);
        this.state = {
          userLists: [],
          pages: '',
          page: '',
          limit: '',
          count: '',
          loading: false,
          currentPage: 1
        }
    }
    componentDidMount() {
      this.fetchData();
      // console.log(this.state.currentPage);
    }

    fetchData = () => {
        var url ="http://localhost:8003/front/getBlogLists";
        this.blogsRes = fetch(url,{
            method: 'GET',
            mode: 'cors',
        }).then(res => {
            console.log(res);
            return res.json();
        }).then(json => {
            console.log('获取的结果', json);
            this.setState({
                articles: json.articles,
                pages: json.pages,
                page: json.page,
                limit: json.limit,
                count: json.count
            });
            return json;
        }).catch(err => {
            console.log('请求错误', err);
        });
    }

    handleInfiniteOnLoad = () => {
      let data = this.state.articles;
      this.setState({
        loading: true,
      });
      if (data.length > 14) {
        message.warning('Infinite List loaded all');
        this.setState({
          hasMore: false,
          loading: false,
        });
        return;
      }
      this.fetchData((res) => {
        data = data.concat(res.results);
        this.setState({
          data,
          loading: false,
        });
      });
    }
    showDetail(id) {
      Event.on('curpage',data =>{
        this.setState({
          currentPage: data
        })
      })
      axios.post('http://localhost:8003/front/detailArticle', {
        id: id,
        page: this.state.currentPage
      })
      .then(function (response) {
        console.log(response);
        Event.emit('detail',response);
      })
      .catch(function (error) {
        console.log(error);
      });
    }
    render() {
      return (
        <div className="demo-infinite-container">
          <InfiniteScroll
            initialLoad={false}
            pageStart={0}
            loadMore={this.handleInfiniteOnLoad}
            hasMore={!this.state.loading && this.state.hasMore}
            useWindow={false}
          >
            <List
              dataSource={this.state.articles}
              renderItem={item => (
                <List.Item key={item._id}>
                  <List.Item.Meta
                    avatar={<Avatar src="https://zos.alipayobjects.com/rmsportal/ODTLcjxAfvqbxHnVXCYX.png" />}
                    title={<a href="https://ant.design">{item.article_title}</a>}
                    description={<p><span>{item.author}</span><span>{item.createTime}</span></p>}
                  />
                  <div>{item.author}</div>
                  <div>
                    <Link to="/articleDetails" onClick={this.showDetail(item._id)}>
                        <Button type="primary" size='small'>查看详情</Button>
                    </Link>
                  </div>
                </List.Item>
              )}
            >
              {this.state.loading && this.state.hasMore && (
                <div className="demo-loading-container">
                  <Spin />
                </div>
              )}
            </List>
          </InfiniteScroll>
        </div>
      );
    }
  }

  export default ArticleItem;
