import React, { Component } from 'react';
import Editor from 'for-editor';
import LeftBar from '../common/protal/leftbar/index'
import { Layout,Button,Modal,Form,Select,Input} from 'antd';
const { Option } = Select;
class WriteArticle extends Component {
  static getDerivedStateFromProps(nextProps) {
    // Should be a controlled component.
    if ('value' in nextProps) {
      return {
        ...(nextProps.value || {}),
      };
    }
    return null;
  }
  constructor() {
    super();
    this.state = {
      valuemd: '',
      visible: false
    }
  }
  // constructor(props) {
  //   super(props);

  //   const value = props.value || {};
  //   this.state = {
  //     number: value.number || 0,
  //     currency: value.currency || 'rmb',
  //   };
  // }
  showModal = () => {
    this.setState({
      visible: true,
    });
  }

  handleOk = (e) => {
    console.log(e);
    this.setState({
      visible: false,
    });
  }

  handleCancel = (e) => {
    console.log(e);
    this.setState({
      visible: false,
    });
  }
  handleSubmit = (e) => {
    e.preventDefault();
    this.props.form.validateFields((err, values) => {
      if (!err) {
        console.log('Received values of form: ', values);
      }
    });
  }

  checkPrice = (rule, value, callback) => {
    if (value.number > 0) {
      callback();
      return;
    }
    callback('Price must greater than zero!');
  }
  componentDidMount(){
      setInterval(()=>{
       this.savemdTohtml();
      },1000);
  }
  handleChange(value) {
    this.setState({
      value
    })
  }
  saveArticle(){
    var mdtohtml = document.querySelector('.for-preview');
    var contentall = mdtohtml.innerHTML;

  }
  render() {
    const { valuemd } = this.state;
    const { getFieldDecorator } = this.props.form;
    // const { size } = this.props;
    // const state = this.state;
    return (
      <Layout>
        <LeftBar></LeftBar>
        <Layout>
          <Editor value={valuemd} onChange={this.handleChange.bind(this)} />   
          <Button type="primary" onClick={this.showModal}>
            保存文章
          </Button>
          <Modal
            title="Basic Modal"
            visible={this.state.visible}
            onOk={this.handleOk}
            onCancel={this.handleCancel}
          >
           <Form layout="inline">
              <Form.Item label="Username">
                {getFieldDecorator('username', {
                  rules: [{ required: true, message: 'Username is required!' }],
                })(<Input />)}
              </Form.Item>
          </Form>
          </Modal>
        </Layout>  
      </Layout>
    )
  }
}

const WrappedDemo = Form.create({ name: 'customized_form_controls' })
export default WriteArticle;
