import React, { Component } from 'react';
import { Layout, Breadcrumb } from 'antd';
import Event from '../../assets/events.js';
import axios from 'axios';
const { Content } = Layout;

class ArticleDetail extends Component {
    constructor(){
        super();
        this.state = {
            cur_id: ''
        }
    }
    componentDidMount(){
        // console.log(e);
        Event.on('detailId',id =>{
            console.log(id);
            this.setState({
                cur_id: id
            });
        })
        this.getDetail();
    }
    getDetail(){
        var that = this;
        axios({
            method:"GET",
            url:'http://localhost:8003/front/detailArticle',
            data:`id=${this.state.cur_id}`
            }).then(function(res){
            console.log(res);
            console.log(document.querySelector('#d'));
            }).then(function(err){
            console.log(err);
        })
    }
    render() {
        return (
            <Layout>
                <div id='d'></div>
            </Layout>
        )
    }
}
export default ArticleDetail;
