import React, { Component } from 'react';
import Editor from 'for-editor';
import LeftBar from '../common/protal/leftbar/index'
import { Layout,Button,Modal,Input} from 'antd';
class WriteArticle extends Component {
  static getDerivedStateFromProps(nextProps) {
    // Should be a controlled component.
    if ('value' in nextProps) {
      return {
        ...(nextProps.value || {}),
      };
    }
    return null;
  }
  constructor() {
    super();
    this.state = {
      valuemd: '',
      visible: false
    }
  }

  showModal = () => {
    this.setState({
      visible: true,
    });
  }

  handleOk = (e) => {
    console.log(e);
    this.setState({
      visible: false,
    });
  }

  handleCancel = (e) => {
    console.log(e);
    this.setState({
      visible: false,
    });
  }

  componentDidMount(){
      setInterval(()=>{
       this.savemdTohtml();
      },1000);
  }
  handleChange(value) {
    this.setState({
      value
    })
  }
  saveArticle(){
    var mdtohtml = document.querySelector('.for-preview');
    var contentall = mdtohtml.innerHTML;
  }
  onChange(e){
    console.log(e);
  };
  render() {
    const { valuemd } = this.state;
    return (
      <Layout>
        <LeftBar></LeftBar>
        <Layout>
          <Editor value={valuemd} onChange={this.handleChange.bind(this)} />   
          <Button type="primary" onClick={this.showModal}>
            保存文章
          </Button>
          <Modal
            title="Basic Modal"
            visible={this.state.visible}
            onOk={this.handleOk}
            onCancel={this.handleCancel}
          >
            <Input placeholder="input with clear icon" allowClear onChange={onChange} />
          </Modal>
        </Layout>  
      </Layout>
    )
  }
}

export default WriteArticle;
