import React from 'react';
import { Table, Input, Popconfirm } from 'antd';

class EditableCell extends React.Component {
  state = {
    value: this.props.value,
    editable: this.props.editable || false,
  }
  componentWillReceiveProps(nextProps) {
    if (nextProps.editable !== this.state.editable) {
      this.setState({ editable: nextProps.editable });
      if (nextProps.editable) {
        this.cacheValue = this.state.value;
      }
    }
    if (nextProps.status && nextProps.status !== this.props.status) {
      if (nextProps.status === 'save') {
        this.props.onChange(this.state.value);
      } else if (nextProps.status === 'cancle') {
        this.setState({ value: this.cacheValue });
        this.props.onChange(this.cacheValue);
      }
    }
  }
  shouldComponentUpdate(nextProps, nextState) {
    return nextProps.editable !== this.state.editable ||
           nextState.value !== this.state.value;
  }
  handleChange(e) {
    const value = e.target.value;
    this.setState({ value });
  }
  render() {
    const { value, editable } = this.state;
    return (
      <div>
        {
          editable ?
            <div>
              <Input
                value={value}
                onChange={e => this.handleChange(e)}
              />
            </div>
            :
            <div className="editable-row-text">
              {value.toString() || ' '}
            </div>
        }
      </div>
    );
  }
}

class BlogLists extends React.Component {
  constructor(props) {
    super(props);
    this.columns = [{
      title: '博客标题',
      dataIndex: 'article_title',
      width: '15%',
      render: (text, record, index) => this.renderColumns(this.state.articles, index, 'article_title', text),
    },{
      title: '博客内容',
      dataIndex: 'article_content',
      width: '25%',
      render: (text, record, index) => this.renderColumns(this.state.articles, index, 'article_content', text),
    }, {
      title: '创建时间',
      dataIndex: 'createTime',
      width: '15%',
      render: (text, record, index) => this.renderColumns(this.state.articles, index, 'createTime', text),
    }, {
      title: '作者',
      dataIndex: 'author',
      width: '15%',
      render: (text, record, index) => this.renderColumns(this.state.articles, index, 'author', text),
    }, {
      title: 'operation',
      dataIndex: 'operation',
      render: (text, record, index) => {
        const { editable } = this.state.articles[index].article_title;
        return (
          <div className="editable-row-operations">
            {
              editable ?
                <span>
                  <a onClick={() => this.editDone(index, 'save')}>Save</a>
                  <Popconfirm title="Sure to cancel?" onConfirm={() => this.editDone(index, 'cancel')}>
                    <a>Cancel</a>
                  </Popconfirm>
                </span>
                :
                <span>
                  <a onClick={() => this.edit(index)}>Edit</a>
                </span>
            }
          </div>
        );
      },
    }];
    this.state = {
      articles: [],
      defaultCurrent: 1,
      current: '',
      total: 3,
      defaultPageSize: 2
    };
  }
  renderColumns(data, index, key, text) {
    const { editable, status } = data[index][key];
    if (typeof editable === 'undefined') {
      return text;
    }
    return (<EditableCell
      editable={editable}
      value={text}
      onChange={value => this.handleChange(key, index, value)}
      status={status}
    />);
  }
  handleChange(key, index, value) {
    const { articles } = this.state;
    articles[index][key].value = value;
    this.setState({ articles });
  }
  edit(index) {
    const { articles } = this.state;
    Object.keys(articles[index]).forEach((item) => {
      if (articles[index][item] && typeof articles[index][item].editable !== 'undefined') {
        articles[index][item].editable = true;
      }
    });
    this.setState({ articles });
  }
  editDone(index, type) {
    const { articles } = this.state;
    Object.keys(articles[index]).forEach((item) => {
      if (articles[index][item] && typeof articles[index][item].editable !== 'undefined') {
        articles[index][item].editable = false;
        articles[index][item].status = type;
      }
    });
    this.setState({ articles }, () => {
      Object.keys(articles[index]).forEach((item) => {
        if (articles[index][item] && typeof articles[index][item].editable !== 'undefined') {
          delete articles[index][item].status;
        }
      });
    });
  }
  onChange = (page) => {
    console.log(page);
    this.setState({
      current: page
    });
    this.fetchData();
  }
  render() {
    const { articles } = this.state;
    const dataSource = articles.map((item) => {
      const obj = {};
      Object.keys(item).forEach((key) => {
        obj[key] = key === 'key' ? item[key] : item[key].value;
      });
      return obj;
    });
    const columns = this.columns;
    return <Table 
                    bordered 
                    dataSource={dataSource} 
                    columns={columns} 
                    pagination={{  // 分页
                      // simple: true,
                      defaultCurrent: this.state.defaultCurrent,
                      // current: this.state.current,
                      total: this.state.total,
                      defaultPageSize: this.state.defaultPageSize,
                      onChange: this.onChange,
                    }}
          />;
  }
  componentDidMount() {
    this.fetchData();
    // console.log(this.state);
  }
  fetchData = () => {
    var url ="http://localhost:8888/front/getBlogLists?page="+this.state.current;
    this.blogsRes = fetch(url,{
        method: 'GET',
        mode: 'cors',
        headers:{
          'Access-Control-Allow-Origin':'*'
        }
    }).then(res => {
        return res.json();
    }).then(json => {
        console.log('获取的结果', json);
        var result = [];
        json.articles.forEach(function(value,index,array){
          var item = value;
          var objall = {};
          objall.key = index;
          // console.log(item);
          Object.keys(item).forEach(function(val,i,arr){
            var obj = {};
              // console.log(val);
              obj.value= item[val];
              obj.editable = false;    
              objall[val] = obj;
          })
          result.push(objall);
        })
        this.setState({
            articles: result,
            // total2: json.pages,
            current: json.page,
            defaultPageSize: json.limit,
            total: json.count
        });
        console.log(this.state.articles);
        return json;
    }).catch(err => {
        console.log('请求错误', err);});
  }
}

export default BlogLists;
