import React, { Component } from 'react';
import Editor from 'for-editor';
import LeftBar from '../common/protal/leftbar/index'
import { Layout,Button,Modal,Input} from 'antd';
import "../../style/front/common.css";
class WriteArticle extends Component {
  constructor() {
    super();
    this.state = {
      value: '',
      visible: false
    }
  }

  showModal = () => {
    this.setState({
      visible: true,
    });
  }

  handleOk = (e) => {
    console.log(e);
    this.setState({
      visible: false,
    });
    this.saveArticle();
  }

  handleCancel = (e) => {
    console.log(e);
    this.setState({
      visible: false,
    });
  }

  componentDidMount(){
      setInterval(()=>{
      //  this.savemdTohtml();
      },1000);
  }
  handleChange(value) {
    this.setState({
      value
    })
  }
  saveArticle(){
    var mdtohtml = document.querySelector('.for-preview');
    var contentall = mdtohtml.innerHTML;
    console.log(contentall);
  }
  render() {
    const { value } = this.state;
    const onChange = (e) => {
      console.log(e);
    };
    return (
      <Layout>
        <LeftBar></LeftBar>
        <Layout>
          <Editor value={value} onChange={this.handleChange.bind(this)} />   
          <Button type="primary" onClick={this.showModal}>
            保存文章
          </Button>
          <Modal
            title="保存为"
            visible={this.state.visible}
            onOk={this.handleOk}
            onCancel={this.handleCancel}
          >
            <Input placeholder="title" onChange={onChange} />
            <Input placeholder="内容简介50字以内" onChange={onChange} />
            <Input placeholder="author" onChange={onChange} />
          </Modal>
        </Layout>  
      </Layout>
    )
  }
}

export default WriteArticle;
