import React, { Component } from 'react';
import { Layout, Breadcrumb } from 'antd';
import Event from '../../assets/events.js';
import axios from 'axios';
const { Content } = Layout;

class ArticleDetail extends Component {
    constructor(props){
        super(props);
        this.state = {
            cur_id: '',
            articleId:''
        }
    }
    componentDidMount(){
        console.log(this.props.location.articleId);
        this.setState({
            articleId: this.props.location.articleId
        })
    }
    getDetail(){
        // var con = document.querySelector('#contentDom');
        // con.innerHTML = this.state.articlesDetail;
        // console.log(this.state.articlesDetail);
      axios.post('http://localhost:8003/front/detailArticle', {
        id: this.state.articleId
      })
      .then(function (response) {
        console.log(response);
        // Event.emit('detail',response);
      })
      .catch(function (error) {
        console.log(error);
      });
    }
    render() {
        return (
            <Layout>
                <div id='contentDom'></div>
            </Layout>
        )
    }
}
export default ArticleDetail;
